            <span css={tw`font-bold`}>Version selector</span>
            <br />
            Select the version you want to run your server with.
          </p>
          <Select
            onChange={(e) => location.replace(`/server/${serverId}/versions/${e.target.value}`)}
            defaultValue={`${stype}/${type}`}
          >
            <option key={1} value={'vanilla/1'}>
              Vanilla
            </option>
            <option key={2} value={'spigot/1'}>
              Spigot
            </option>
            <option key={3} value={'paper/1'}>
              Paper
            </option>
            <option key={4} value={'purpur/1'}>
              Purpur
            </option>
            <option key={5} value={'sponge/2'}>
              Sponge
            </option>
            <option key={6} value={'bungeecord/1'}>
              Bungeecord
            </option>
            <option key={7} value={'waterfall/1'}>
              Waterfall
            </option>
            <option key={8} value={'velocity/1'}>
              Velocity
            </option>
            <option key={9} value={'forge/2'}>
              Forge
            </option>
            <option key={10} value={'fabric/2'}>
              Fabric
            </option>
            <option key={11} value={'mohist/2'}>
              Mohist
            </option>
            <option key={12} value={'magma/2'}>
              Magma
            </option>
            {custom && (
              <option key={13} value={'others/3'}>
                Others
              </option>
            )}
