import axios from 'axios';
import getFileUploadUrl from '@/api/server/files/getFileUploadUrl';
import tw from 'twin.macro';
import Button from '@/components/elements/Button';
import React, { useEffect, useRef, useState } from 'react';
import styled from 'styled-components';
import { ModalMask } from '@/components/elements/Modal';
import Fade from '@/components/elements/Fade';
import useEventListener from '@/plugins/useEventListener';
import SpinnerOverlay from '@/components/elements/SpinnerOverlay';
import useFlash from '@/plugins/useFlash';
import useFileManagerSwr from '@/plugins/useFileManagerSwr';
import { ServerContext } from '@/state/server';
import { WithClassname } from '@/components/types';

const InnerContainer = styled.div`
  max-width: 600px;
  ${tw`bg-black w-full border-4 border-primary-500 border-dashed rounded p-10 mx-10`}
`;

export default ({ className }: WithClassname) => {
  const fileUploadInput = useRef<HTMLInputElement>(null);
  const uuid = ServerContext.useStoreState((state) => state.server.data!.uuid);
  const [visible, setVisible] = useState(false);
  const [loading, setLoading] = useState(false);
  const { mutate } = useFileManagerSwr();
  const { clearFlashes, clearAndAddHttpError } = useFlash();
  const directory = '/plugins';
  useEventListener(
    'dragenter',
    (e) => {
      e.stopPropagation();
      setVisible(true);
    },
    true
  );

  useEventListener(
    'dragexit',
    (e) => {
      e.stopPropagation();
      setVisible(false);
    },
    true
  );

  useEffect(() => {
    if (!visible) return;

    const hide = () => setVisible(false);

    window.addEventListener('keydown', hide);
    return () => {
      window.removeEventListener('keydown', hide);
    };
  }, [visible]);

  const onFileSubmission = (files: FileList) => {
    const form = new FormData();
    Array.from(files).forEach((file) => form.append('files', file));

    setLoading(true);
    clearFlashes('files');
    getFileUploadUrl(uuid)
      .then((url) =>
        axios.post(`${url}&directory=${directory}`, form, {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        })
      )
      .then(() => mutate())
      .catch((error) => {
        console.error(error);
        clearAndAddHttpError({ error, key: 'files' });
      })
      .then(() => setVisible(false))
      .then(() => setLoading(false));
  };

  return (
    <>
      <Fade appear in={visible} timeout={75} key={'upload_modal_mask'} unmountOnExit>
        <ModalMask
          onClick={() => setVisible(false)}
          onDragOver={(e) => e.preventDefault()}
          onDrop={(e) => {
            e.preventDefault();
            e.stopPropagation();

            setVisible(false);
            if (!e.dataTransfer?.files.length) return;

            onFileSubmission(e.dataTransfer.files);
          }}
        >
          <div css={tw`w-full flex items-center justify-center`} style={{ pointerEvents: 'none' }}>
            <InnerContainer>
              <p css={tw`text-lg text-neutral-200 text-center`}>Drag and drop files to upload a plugin.</p>
            </InnerContainer>
          </div>
        </ModalMask>
      </Fade>
      <SpinnerOverlay visible={loading} size={'large'} fixed />
      <input
        type={'file'}
        ref={fileUploadInput}
        css={tw`hidden`}
        onChange={(e) => {
          if (!e.currentTarget.files) return;

          onFileSubmission(e.currentTarget.files);
          if (fileUploadInput.current) {
            fileUploadInput.current.files = null;
          }
        }}
      />
      <Button
        className={className}
        onClick={() => {
          fileUploadInput.current ? fileUploadInput.current.click() : setVisible(true);
        }}
      >
        Upload
      </Button>
    </>
  );
};

import React, { useCallback, useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faDownload, faExternalLinkAlt, faListAlt, faStar, faTrash } from '@fortawesome/free-solid-svg-icons';
import tw from 'twin.macro';
import useFlash from '@/plugins/useFlash';
import GreyRowBox from '@/components/elements/GreyRowBox';
import Button from '@/components/elements/Button';
import deleteFiles from '@/api/server/files/deleteFiles';
import { ApplicationStore } from '@/state';
import { Actions, useStoreActions } from 'easy-peasy';
import installPlugin from '@/api/server/mcplugins/installPlugin';
import uninstallPlugin from '@/api/server/mcplugins/uninstallPlugin';
import Select from '@/components/elements/Select';
import { formatDistanceToNow } from 'date-fns';
import Modal from '@/components/elements/Modal';

interface Props {
  minecraftPlugins: any;
  className?: string;
  uuid: string;
  type: string;
  nameoftype: string;
}

export default ({ minecraftPlugins, className, uuid, type, nameoftype }: Props) => {
  const { clearAndAddHttpError } = useFlash();
  const [disable, setDisable] = useState(false);
  const [Installed, setInstalled] = useState(type === '4' ? 1 : minecraftPlugins.installed);

  const { addFlash, clearFlashes } = useStoreActions((actions: Actions<ApplicationStore>) => actions.flashes);
  function clear() {
    clearFlashes();
  }
  let iconurl = 'https://static.spigotmc.org/styles/spigot/xenresource/resource_icon.png';
  if (type !== '5') {
    if (minecraftPlugins.icon.url !== '' && minecraftPlugins.icon.url !== undefined) {
      iconurl = type === '1' ? 'https://www.spigotmc.org/' + minecraftPlugins.icon.url : minecraftPlugins.icon.url;
    }
    if (type === '4' && minecraftPlugins.icon[0].url !== '') {
      iconurl = minecraftPlugins.icon[0].url;
    }
    if (type === '3') {
      iconurl = 'https://polymart.org/style/logo.png';
    }
  }
  console.log(minecraftPlugins);
  const [visible, setVisible] = useState(false);
  let versionlist = [{ id: '2' }];
  let file = '';
  if (type === '1') {
    versionlist = minecraftPlugins.versionslist[0]
      .sort(function (a: { name: string }, b: { name: string }) {
        return a.name.localeCompare(b.name, 'en', { sensitivity: 'base' });
      })
      .reverse();
    file = versionlist[0]?.id;
  }

  const updateSelectedFile = useCallback(
    (v: React.ChangeEvent<HTMLSelectElement>) => {
      file = v.currentTarget.value;
    },
    [uuid, file]
  );

  const testedVersions = [];

  for (const versions in minecraftPlugins.testedVersions) {
    testedVersions.push(minecraftPlugins.testedVersions[versions]);
  }
  const install = () => {
    setDisable(true);
    if (minecraftPlugins.file.type === 'external') {
      addFlash({
        key: 'server:minecraftMcPlugins' + nameoftype,
        type: 'warning',
        message:
          "This plugins can't be installed because the plugins files was not on spigotmc please install it manualy from this url https://www.spigotmc.org/" +
          minecraftPlugins.file.url +
          '. You can also see if this plugins are on bukkit with the bukkit tab.',
      });
      setDisable(false);
      setTimeout(clear, 15000);
    } else {
      installPlugin(
        uuid,
        minecraftPlugins.name,
        type === '1'
          ? `https://cdn.spiget.org/file/spiget-resources/${minecraftPlugins.id}.jar?version=${file}`
          : minecraftPlugins.downloadlink
      )
        .then(() => {
          addFlash({
            key: 'server:minecraftMcPlugins' + nameoftype,
            type: 'success',
            message: 'Plugins installed successfully',
          });
          setDisable(false);
          setInstalled(1);
          setTimeout(clear, 3000);
        })
        .catch(function (error) {
          setDisable(false);
          addFlash({
            key: 'server:minecraftMcPlugins' + nameoftype,
            type: 'error',
            message: `This plugin has too large a size, it must be installed manually (${error})`,
          });
          setTimeout(clear, 3000);
        });
    }
  };
  const uninstall = () => {
    setDisable(true);
    if (minecraftPlugins.file.type === 'external') {
      addFlash({
        key: 'server:minecraftMcPlugins' + nameoftype,
        type: 'warning',
        message:
          "This plugins can't be removed because the plugins files was not on spigotmc please remove it manualy ",
      });
      setDisable(false);
      setTimeout(clear, 15000);
    } else {
      deleteFiles(uuid, '/plugins', [`${minecraftPlugins.name}.jar`])
        .then(() => {
          uninstallPlugin(uuid, minecraftPlugins.name)
            .then(() => {
              addFlash({
                key: 'server:minecraftMcPlugins' + nameoftype,
                type: 'success',
                message: 'Plugins removed successfully',
              });
              setDisable(false);
              setInstalled(0);
              setTimeout(clear, 3000);
            })
            .catch(function (error) {
              setDisable(false);
              clearAndAddHttpError({ key: 'minecraftMcPlugins', error });
            });
        })
        .catch(function (error) {
          setDisable(false);
          clearAndAddHttpError({ key: 'minecraftMcPlugins', error });
        });
    }
  };
  return (
    <GreyRowBox css={tw`flex-wrap md:flex-nowrap items-center`} className={className}>
      {type === '1' && (
        <Modal
          visible={visible}
          onDismissed={() => {
            setVisible(false);
          }}
        >
          <div css={tw`flex`}>
            <div css={tw`flex items-center text-sm mb-1 cursor-pointer `}>
              <img
                css={tw`w-10 h-10 rounded-lg cursor-pointer mr-4`}
                onClick={() => window.open(`https://spigotmc.org/resources/bagou.${minecraftPlugins.id}`, '_blank')}
                alt={minecraftPlugins.name}
                src={iconurl}
              />

              <div>
                <h1 css={tw`text-lg`}>{minecraftPlugins.name}</h1>
                <p css={tw`text-sm`}>{minecraftPlugins.tag}</p>
              </div>
            </div>
            <div css={tw`text-base  mx-6`}>
              <p>
                Author:{' '}
                <a
                  css={tw`text-blue-400`}
                  href={`https://spigotmc.org/members/${minecraftPlugins.author.id}`}
                  target={'_blank'}
                  rel='noreferrer'
                >
                  https://spigotmc.org/members/{minecraftPlugins.author.id}
                </a>
              </p>
              {minecraftPlugins.contributors !== null ?? <p>Contributor: {minecraftPlugins.contributors}</p>}
              <p>Versions: {minecraftPlugins.testedVersions.join()}</p>
              <p>Downloads: {minecraftPlugins.downloads}</p>
              <p>
                Released:{' '}
                {formatDistanceToNow(new Date(minecraftPlugins.releaseDate * 1000), {
                  includeSeconds: true,
                  addSuffix: true,
                })}
              </p>
              <p>
                Updated:{' '}
                {formatDistanceToNow(new Date(minecraftPlugins.updateDate * 1000), {
                  includeSeconds: true,
                  addSuffix: true,
                })}
              </p>
            </div>
            <div css={tw`flex-none mt-2 my-auto`}>
              {Installed === 0 && (
                <Select onChange={updateSelectedFile} defaultValue={minecraftPlugins.versionslist[0][0]?.id}>
                  {versionlist.map((files: any) => (
                    <option key={Math.random()} value={files.id}>
                      {files.name.startsWith('V') || files.name.startsWith('v') ? files.name : `Version ${files.name}`}
                    </option>
                  ))}
                </Select>
              )}
              <Button
                type={'button'}
                color={Installed === 1 ? 'red' : 'green'}
                css={tw`w-36 mt-6`}
                onClick={() => {
                  if (Installed === 0) {
                    install();
                  } else {
                    uninstall();
                  }
                }}
                title={Installed === 1 ? 'Uninstall' : 'Install'}
                disabled={disable}
                isLoading={disable}
              >
                <FontAwesomeIcon icon={Installed === 1 ? faTrash : faDownload} />{' '}
                {Installed === 1 ? 'Uninstall' : 'Install'}
              </Button>
            </div>
          </div>
        </Modal>
      )}
      <div css={tw`flex items-center truncate w-full md:flex-1`}>
        <div css={tw`flex flex-col truncate`}>
          <div
            css={tw`flex items-center text-sm mb-1 cursor-pointer `}
            onClick={() =>
              window.open(
                type === '1'
                  ? `https://spigotmc.org/resources/bagou.${minecraftPlugins.id}`
                  : type === '2'
                  ? `https://dev.bukkit.org${minecraftPlugins.links.discussion}`
                  : minecraftPlugins.links.discussion,
                '_blank'
              )
            }
          >
            <img css={tw`w-10 h-10 rounded-lg`} alt={minecraftPlugins.name} src={iconurl} />

            <p css={tw`ml-2`}>
              {minecraftPlugins.name}
              <br />
              {type === '1' && (
                <>
                  <FontAwesomeIcon
                    css={`
                      ${minecraftPlugins.rating.average >= 0.5 ? 'color: yellow;' : 'color: darkgray'}
                    `}
                    icon={faStar}
                  />
                  <FontAwesomeIcon
                    css={`
                      ${minecraftPlugins.rating.average >= 1.5 ? 'color: yellow;' : 'color: darkgray'}
                    `}
                    icon={faStar}
                  />
                  <FontAwesomeIcon
                    css={`
                      ${minecraftPlugins.rating.average >= 2.5 ? 'color: yellow;' : 'color: darkgray'}
                    `}
                    icon={faStar}
                  />
                  <FontAwesomeIcon
                    css={`
                      ${minecraftPlugins.rating.average >= 3.5 ? 'color: yellow;' : 'color: darkgray'}
                    `}
                    icon={faStar}
                  />
                  <FontAwesomeIcon
                    css={`
                      ${minecraftPlugins.rating.average >= 4.5 ? 'color: yellow;' : 'color: darkgray'}
                    `}
                    icon={faStar}
                  />
                </>
              )}
            </p>
          </div>
          {Installed === 1 ? (
            minecraftPlugins.installdate ? (
              <p css={tw`text-sm mt-2`}>Installed the {minecraftPlugins.installdate}</p>
            ) : (
              <p css={tw`text-sm mt-2`}>Installed today</p>
            )
          ) : (
            <>
              {minecraftPlugins.premium ? (
                <p css={tw`mt-1 md:mt-0 text-xs truncate text-yellow-500`}>PREMIUM ADDON</p>
              ) : (
                <>
                  {minecraftPlugins.file.type === 'external' ? (
                    <p css={tw`mt-1 md:mt-0 text-xs truncate text-yellow-500`}>EXTERNAL DOWNLOAD</p>
                  ) : (
                    <p css={tw`mt-1 md:mt-0 text-xs truncate cursor-help`} title={minecraftPlugins.tag}>
                      {minecraftPlugins.tag}
                    </p>
                  )}
                </>
              )}
            </>
          )}
        </div>
      </div>
      {type === '1' && (
        <div
          css={tw`flex-1 hidden md:block md:flex-none md:ml-5 md:w-48 mt-4 md:mt-0 md:text-center cursor-help`}
          title={`Versions: ${minecraftPlugins.testedVersions.join()}`}
        >
          <p css={tw`text-2xs text-neutral-500 uppercase mt-1`}>Versions</p>
          <p>From: {testedVersions[0]}</p>
          <p>To: {testedVersions[testedVersions.length - 1]}</p>
        </div>
      )}
      <div css={tw`mt-4 md:mt-0 ml-6`} style={{ marginRight: '-0.5rem' }}>
        <>
          {minecraftPlugins.file.type === 'external' || minecraftPlugins.premium ? (
            <Button
              type={'button'}
              color={'primary'}
              onClick={() => {
                window.open(`https://spigotmc.org/resources/bagou.${minecraftPlugins.id}`);
              }}
              title={'Go to'}
              disabled={disable}
              isLoading={disable}
            >
              <FontAwesomeIcon icon={faExternalLinkAlt} /> Go to
            </Button>
          ) : type === '5' ? (
            <Button
              type={'button'}
              color={'red'}
              css={tw`mb-2`}
              onClick={() => {
                uninstall();
              }}
              title={'Uninstall'}
              disabled={disable}
              isLoading={disable}
            >
              <FontAwesomeIcon icon={faTrash} /> Uninstall
            </Button>
          ) : (
            <div css={type === '1' ? tw`grid grid-rows-2` : tw`grid`}>
              <Button
                type={'button'}
                color={Installed === 1 ? 'red' : 'green'}
                css={tw`mb-2`}
                onClick={() => {
                  if (Installed === 0) {
                    install();
                  } else {
                    uninstall();
                  }
                }}
                title={Installed === 1 ? 'Uninstall' : 'Install'}
                disabled={disable}
                isLoading={disable}
              >
                <FontAwesomeIcon icon={Installed === 1 ? faTrash : faDownload} />{' '}
                {Installed === 1 ? 'Uninstall' : 'Install'}
              </Button>
              {type === '1' && (
                <Button
                  type={'button'}
                  color={'primary'}
                  css={tw`mt-2`}
                  onClick={() => setVisible(true)}
                  title={'View'}
                  disabled={disable}
                  isLoading={disable}
                >
                  <FontAwesomeIcon icon={faListAlt} /> View
                </Button>
              )}
            </div>
          )}
        </>
      </div>
    </GreyRowBox>
  );
};

